.heading-tertiary {
  font-size: var(--heading-tertiary-font-size);
  line-height: 1.5;
  text-align: center;
  color: var(--color-text-gray);
}

.paragraph {
  font-size: var(--default-font-size);
  line-height: 1.5;
}

.section-margin-bottom {
  margin-bottom: var(--section-margin-bot);
}

.hr {
  width: 100%;
  height: auto;
}

/* Components */
.btn:link, .btn:visited {
  position: relative;
  display: inline-flex;
  align-items: center;
  padding: 1.6rem 4.2rem;
  border-radius: 10rem;
  font-size: 2rem;
  text-transform: capitalize;
  cursor: pointer;
  transition: all 0.4s;
}
.btn::after {
  content: "";
  display: inline-block;
  height: 100%;
  width: 100%;
  position: absolute;
  top: 0;
  left: 0;
  border-radius: 10rem;
  z-index: -1;
  transition: all 0.4s;
}
.btn--wave::before {
  content: "";
  display: inline-block;
  width: 3rem;
  height: 3rem;
  margin-right: 2rem;
  background-image: url("/2a2e06b2a7365ec5b6bd91ce88dd8869.svg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: top center;
}
.btn--blue {
  background-color: var(--color-primary);
  color: var(--color-text-white);
  border: none;
}
.btn--blue::after {
  background-color: var(--color-primary);
}
.btn--blue:hover {
  transform: translateY(-3px);
  box-shadow: 0 1rem 2rem rgba(61, 55, 241, 0.6);
}
.btn--blue:hover::after {
  transform: scaleX(1.4) scaleY(1.6);
  opacity: 0;
}
.btn--white {
  border: 0.2rem solid var(--color-text-white);
  background-color: transparent;
  color: var(--color-text-white);
}
.btn--white:hover {
  background-color: var(--color-text-white);
  color: var(--color-text-black);
}
.btn--orange, .btn--orange:link, .btn--orange:visited {
  padding: 1.6rem 6rem;
  font-family: var(--font-title);
  color: var(--color-text-white);
  background-color: var(--btn-log-in);
}
.btn--shadow {
