sources.styles/components/_popup.scss:
.popup {
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    left: 0;
    background-color: rgba(03, 03, 03, .8);
    z-index: 9999;
    opacity: 0;
    visibility: hidden;
    transition: all .3s;

    @supports (-webkit-backdrop-filter: blur(10px)) or (backdrop-filter: blur(10px)) {
        -webkit-backdrop-filter: blur(10px);
        backdrop-filter: blur(10px);
        background-color: rgba(03, 03, 03, .3);
    }

    &__content {
        @include absCenter;

        width: calc(100% - 10%);
        min-height: 30rem;
        padding-top: 1rem;
        background-color: var(--color-text-white);
        box-shadow: 0 2rem 4rem rgba(03, 03, 03, .2);
        border-radius: 3px;
        overflow: hidden;
        opacity: 0;
        transform: translate(-50%, -50%) scale(.25);
        transition: all .5s .2s;

        @include respond(desktop) {
            min-height: 60rem;
        }
    }

    &__navigation {
        padding: 1rem 0;
    }

    &__video {
        display: block;
        width: 100%;
        min-height: 30rem;
        height: 100%;

        @include respond(desktop) {
            min-height: 60rem;
        }
    }

    //Open states
    &:target {
        opacity: 1;
        visibility: visible;
    }

    &:target &__content {
        opacity: 1;
        transform: translate(-50%, -50%) scale(1);
    }

    &__close {
        &:link,
        &:visited {
            color: #030303;
            position: absolute;
            top: 1rem;
            right: 2rem;
            font-size: 4rem;
            text-decoration: none;
            display: inline-block;
            transition: all .2s;
            line-height: 1;
        }

        &:hover {
            color: var(--color-primary);
        }
    }
}

